// Signin.js
import React, { useState } from "react";
import "./Signin.css";

function Signin() {
  const [isFocused, setIsFocused] = useState(false);

  const handleFocus = () => {
    setIsFocused(true);
  };

  const handleBlur = (e) => {
    if (!e.target.value) {
      setIsFocused(false);
    }
  };

  return (
    <div className="signin-bg">
      <div className="signin-box">
        <div
          className={`signin-overlay ${
            isFocused ? "animated-placeholder" : ""
          }`}
        ></div>
        <h1 className="form">Log In</h1>
        <hr className="line" />
        <input
          className={`signin-input ${isFocused ? "animated-placeholder" : ""}`}
          type="text"
          placeholder="Username"
          onFocus={handleFocus}
          onBlur={handleBlur}
        />
        <br />
        <input
          className={`signin-input ${isFocused ? "animated-placeholder" : ""}`}
          type="password"
          placeholder="Password"
          onFocus={handleFocus}
          onBlur={handleBlur}
        />
        <br />
        <button className="signin-button">Log in</button>
      </div>
    </div>
  );
}

export default Signin;
