import React, { useState, useRef, useEffect } from "react";
import "../Signin/Signin.css";
import Navbar1 from "../Navbar/Navbar";
import { useLocation, useNavigate } from "react-router-dom";

function Verification() {
  const location = useLocation();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    otp1: "",
    otp2: "",
    otp3: "",
    otp4: "",
    otp5: "",
    otp6: "",
  });
  const [resendEnabled, setResendEnabled] = useState(true);
  const [timer, setTimer] = useState(60);

  const inputRefs = {
    otp1: useRef(null),
    otp2: useRef(null),
    otp3: useRef(null),
    otp4: useRef(null),
    otp5: useRef(null),
    otp6: useRef(null),
  };

  useEffect(() => {
    let interval;

    if (resendEnabled && timer > 0) {
      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
      }, 1000);
    }

    return () => {
      clearInterval(interval);
    };
  }, [resendEnabled, timer]);

  const getNextInputName = (currentInputName) => {
    const keys = Object.keys(formData);
    const currentIndex = keys.indexOf(currentInputName);

    // Check if the current input is not the last one
    if (currentIndex < keys.length - 1) {
      return keys[currentIndex + 1];
    }

    return null; // If it's the last input, return null
  };

  const handleInputChange = (event, inputName) => {
    const { value } = event.target;

    // Check if the value is empty and backspace was pressed
    if (value === "" && event.key === "Backspace") {
      const prevInputName = getNextInputName(inputName);
      if (prevInputName) {
        // Clear the current input and move focus to the previous one
        setFormData((prevData) => ({
          ...prevData,
          [inputName]: value,
        }));
        inputRefs[prevInputName].current.focus();
      }
    } else {
      // Limit the input length to one character
      const limitedValue = value.slice(0, 1);

      // Update the current input value
      setFormData((prevData) => ({
        ...prevData,
        [inputName]: limitedValue,
      }));

      // Move focus to the next input
      const nextInputName = getNextInputName(inputName);
      if (nextInputName !== null) {
        inputRefs[nextInputName].current.focus();
      }
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const fotp =
      formData.otp1 +
      formData.otp2 +
      formData.otp3 +
      formData.otp4 +
      formData.otp5 +
      formData.otp6;
    console.log(fotp);
  };

  const handleClear = () => {
    const firstInputName = Object.keys(formData)[0];
    const firstInputRef = inputRefs[firstInputName].current;

    const clearedFormData = Object.keys(formData).reduce((acc, key) => {
      acc[key] = "";
      return acc;
    }, {});

    setFormData(clearedFormData);

    if (firstInputRef) {
      firstInputRef.focus();
    }
  };

  const handleResend = () => {
    setResendEnabled(false);
    setTimer(60);

    // TODO: Add logic for resending OTP

    // Enable resend link after 60 seconds
    setTimeout(() => {
      setResendEnabled(true);
    }, 60000);
  };

  return (
    <div className="signin-bg">
      <div className="signin-box">
        <h1 className="form">
          <div style={{ color: "#75006f" }}>
            <span>Enter OTP</span>
          </div>
        </h1>
        <br />
        <h5 style={{ color: "red" }}>Enter the OTP sent to your email</h5>
        <br />
        <div>
          {Object.keys(formData).map((key, index) => (
            <React.Fragment key={key}>
              <input
                ref={inputRefs[key]}
                name={key}
                type="number"
                style={{
                  width: "40px",
                  height: "50px",
                  fontSize: "20px",
                  border: "2px solid #75006f",
                  outline: "none",
                  textAlign: "center", // Center the text
                }}
                value={formData[key]}
                onChange={(e) => handleInputChange(e, key)}
                maxLength="1"
              />
              &nbsp; {/* Add a space here */}
            </React.Fragment>
          ))}
        </div>
        <br />
        <button className="signin-button" type="button" onClick={handleClear}>
          Clear
        </button>
        <br />
        <button className="signin-button" type="button" onClick={handleSubmit}>
          Submit
        </button>
        <br />
        <button
          className="signin-button"
          type="button"
          onClick={handleResend}
          disabled={!resendEnabled}
        >
          Resend
        </button>
        <br />
        {!resendEnabled && <p>Resend OTP in {timer} seconds</p>}
      </div>
    </div>
  );
}

export default Verification;
