import React, { useState } from "react";
import "../Signin/Signin.css";
import "./Signup.css";
import Navbar1 from "../Navbar/Navbar";
import Modal from "../Modal/Modal";
import { createPortal } from "react-dom";

function Signup() {
  const [modalOpen, setModalOpen] = useState(false);
  const [message, setMessage] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    username: "",
    email: "",
    password: "",
    cpass: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    // if (!emailRegex.test(formData.email)) {
    //   console.log("Invalid email syntax");
    //   return;
    // }
    // if (formData.password.length < 8) {
    //   console.log("Password must be at least 8 characters long");
    //   return;
    // }
    // const lowercaseRegex = /[a-z]/;
    // if (!lowercaseRegex.test(formData.password)) {
    //   console.log("Password must contain at least one lowercase letter");
    //   return;
    // }

    // const digitRegex = /\d/;
    // if (!digitRegex.test(formData.password)) {
    //   console.log("Password must contain at least one digit");
    //   return;
    // }

    // const specialCharRegex = /[!@#$%^&*(),.?":{}|<>]/;
    // if (!specialCharRegex.test(formData.password)) {
    //   console.log("Password must contain at least one special character");
    //   return;
    // }
    setModalOpen(true);
    console.log(formData);
  };

  const handleButtonClick = (value) => {
    setModalOpen(false);
    setMessage(value);
  };

  return (
    <div className="signin-bg">
      <br />
      <Navbar1 />
      <div className="signup-box">
        <h1 className="form">
          <div className="waviy" style={{ color: "#75006f" }}>
            <span style={{ "--i": 1 }}>S</span>
            <span style={{ "--i": 2 }}>i</span>
            <span style={{ "--i": 3 }}>g</span>
            <span style={{ "--i": 4 }}>n</span>
            &nbsp;
            <span style={{ "--i": 5 }}>U</span>
            <span style={{ "--i": 6 }}>p</span>
          </div>
        </h1>
        <div className="line"></div>
        <br />
        <form onSubmit={handleSubmit}>
          <div className="signin-inputbox">
            <input
              className="signin-input"
              type="text"
              name="name"
              required="required"
              value={formData.name}
              onChange={handleInputChange}
            />
            <span>Name</span>
          </div>
          <br />
          <div className="signin-inputbox">
            <input
              className="signin-input"
              type="text"
              name="username"
              required="required"
              value={formData.username}
              onChange={handleInputChange}
            />
            <span>Username</span>
          </div>
          <br />
          <div className="signin-inputbox">
            <input
              className="signin-input"
              type="text"
              name="email"
              required="required"
              value={formData.email}
              onChange={handleInputChange}
            />
            <span>Email</span>
          </div>
          <br />
          <div className="signin-inputbox">
            <input
              className="signin-input"
              type="password"
              name="password"
              required="required"
              value={formData.password}
              onChange={handleInputChange}
            />
            <span>Password</span>
          </div>
          <br />
          <div className="signin-inputbox">
            <input
              className="signin-input"
              type="password"
              name="cpass"
              required="required"
              value={formData.cpass}
              onChange={handleInputChange}
            />
            <span>Confirm Password</span>
          </div>
          <br />
          <center>
            <button className="signin-button" type="submit">
              Sign up
            </button>
          </center>
        </form>
        <br />
        <div style={{ color: "black", fontSize: "20px" }}>
          Having an account already?{" "}
          <a href="/" style={{ color: "#75006f", textDecoration: "none" }}>
            Log in
          </a>{" "}
          Here
        </div>
        {modalOpen &&
          createPortal(
            <Modal
              closeModal={handleButtonClick}
              onSubmit={handleButtonClick}
              onCancel={handleButtonClick}
            >
              <h1>This is a modal</h1>
              <br />
              <p>This is the modal description</p>
            </Modal>,
            document.body
          )}
      </div>
    </div>
  );
}

export default Signup;
