import React, { useState } from "react";
import "./Signin.css";
import Navbar1 from "../Navbar/Navbar";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Modal from "./Modal";

function Signin() {
  const [open, setOpen] = useState(false);
  const [title, setTitle] = useState("");
  const [subtitle, setSubtitle] = useState("");
  const [status, setStatus] = useState(true);
  const [data, setData] = useState();
  const location = useLocation();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    console.log(name, value); // Add this line to check the input values
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const url = "http://localhost:5500/User-Data/login";
    const username = formData.username;
    axios
      .post(url, formData)
      .then(async (res) => {
        if (res.status === 200) {
          const obj = { username };
          const url1 = "http://localhost:5500/User-Data/data";
          const response = await axios.post(url1, obj);
          setData(response.data);
          if (response.data.verified === false) {
            setTitle("Verification Error");
            setSubtitle(
              "Your account is not verified. Please verify your account."
            );
            setStatus(true);
            setOpen(true);
            return;
          }
        }
        console.log(formData);
        navigate("/dashboard", { state: { username } });
      })
      .catch((err) => {
        if (err.response && err.response.status === 400) {
          setTitle("Sign Up Error!!");
          setSubtitle(err.response.data);
          setStatus(false);
          setOpen(true);
        } else {
          setTitle("Sign Up Error!!");
          setSubtitle(err.response.data);
          setStatus(false);
          setOpen(true);
        }
      });
  };

  const cancel = () => {
    setOpen(false);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSubmit(event);
    }
  };

  const verify = async () => {
    try {
      let obj = { username: formData.username };
      console.log(obj);
      const url = "http://localhost:5500/User-Data/data";
      const response = await axios.post(url, obj);
      setData(response.data);
      const verified = data.verified;
      if (verified !== true) {
        const username = data.username;
        const email = data.email;

        obj = { username, email };
        const resendUrl =
          "http://localhost:5500/User-Data/resendOTPVerificationCode";
        const res = await axios.post(resendUrl, obj);

        if (res.status === 200) {
          setTitle("OTP Resent Successfully!!");
          setSubtitle("Enter the OTP to successfully Sign Up.");
          setStatus(true);
          setOpen(true);

          // Redirect to the "/verification" page after successful OTP resend
          navigate("/verification", { state: { username } });
        }
      }
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setTitle("Sign Up Error!!");
        setSubtitle(error.response.data);
        setStatus(false);
        setOpen(true);
      } else {
        console.error("An error occurred:", error);
        setTitle("Sign Up Error!!");
        setSubtitle("An error occurred while resending OTP.");
        setStatus(false);
        setOpen(true);
      }
    }
  };

  return (
    <>
      <div className="signin-bg">
        <br />
        <Navbar1 />
        <div className="signin-box">
          <h1 className="form">
            <div className="waviy" style={{ color: "#75006f" }}>
              <span style={{ "--i": 1 }}>L</span>
              <span style={{ "--i": 2 }}>o</span>
              <span style={{ "--i": 3 }}>g</span>
              &nbsp;
              <span style={{ "--i": 4 }}>i</span>
              <span style={{ "--i": 5 }}>n</span>
            </div>
          </h1>
          <div className="line"></div>
          <br />
          <form onSubmit={handleSubmit}>
            <div className="signin-inputbox">
              <input
                className="signin-input"
                type="text"
                required="required"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
              />
              <span>Username</span>
            </div>
            <br />
            <div className="signin-inputbox">
              <input
                className="signin-input"
                type="password"
                required="required"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
              />
              <span>Password</span>
            </div>
            <br />
            <center>
              <center
                style={{
                  display: "flex",
                  flexDirection: "row",
                  gap: "10px",
                  justifyContent: "center",
                }}
              >
                <button className="signin-button" type="submit">
                  Log in
                </button>
                <button
                  className="signin-button"
                  style={{ fontSize: "15px" }}
                  onClick={() => {
                    verify();
                  }}
                >
                  Verify Account
                </button>
              </center>
              <div style={{ color: "black", fontSize: "20px" }}>
                <br />
                Forgot{" "}
                <a
                  href="/forgot-password"
                  style={{ color: "#75006f", textDecoration: "none" }}
                >
                  Password
                </a>
                ?
              </div>
              <div style={{ color: "black", fontSize: "20px" }}>
                New to website{" "}
                <a
                  href="/signup"
                  style={{ color: "#75006f", textDecoration: "none" }}
                >
                  Sign Up
                </a>{" "}
                Here
              </div>
            </center>
          </form>
        </div>
      </div>
      <Modal
        open={open}
        closeDialog={cancel}
        title={title}
        subtitle={subtitle}
        deleteFunction={verify}
      />
    </>
  );
}

export default Signin;
