import React, { useState, useRef } from "react";
import "../Signin/Signin.css";
import Navbar1 from "../Navbar/Navbar";
import { useLocation, useNavigate } from "react-router-dom";

function Verification() {
  const location = useLocation();
  let username = location.state ? location.state.username : null;
  const [formData, setFormData] = useState({
    otp1: "",
    otp2: "",
    otp3: "",
    otp4: "",
    otp5: "",
    otp6: "",
  });

  const inputRefs = {
    otp1: useRef(null),
    otp2: useRef(null),
    otp3: useRef(null),
    otp4: useRef(null),
    otp5: useRef(null),
    otp6: useRef(null),
  };

  const getNextInputName = (currentInputName) => {
    const keys = Object.keys(formData);
    const currentIndex = keys.indexOf(currentInputName);

    // Check if the current input is not the last one
    if (currentIndex < keys.length - 1) {
      return keys[currentIndex + 1];
    }

    return null; // If it's the last input, return null
  };

  const handleInputChange = (event, inputName) => {
    const { value } = event.target;

    // Check if the value is empty and backspace was pressed
    if (value === "" && event.key === "Backspace") {
      const prevInputName = getNextInputName(inputName);
      if (prevInputName) {
        // Clear the current input and move focus to the previous one
        setFormData((prevData) => ({
          ...prevData,
          [inputName]: value,
        }));
        inputRefs[prevInputName].current.focus();
      }
    } else {
      // Move focus to the next input
      const nextInputName = getNextInputName(inputName);
      if (nextInputName) {
        setFormData((prevData) => ({
          ...prevData,
          [inputName]: value,
        }));
        const nextInputRef = inputRefs[nextInputName].current;
        nextInputRef.focus();
        nextInputRef.setSelectionRange(1, 1); // Set the selection range to the end
      }
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    console.log(formData);
    // Add your logic to handle the OTP submission
  };

  const handleClear = () => {
    const firstInputName = Object.keys(formData)[0];
    const firstInputRef = inputRefs[firstInputName].current;

    setFormData({
      otp1: "",
      otp2: "",
      otp3: "",
      otp4: "",
      otp5: "",
      otp6: "",
    });

    if (firstInputRef) {
      firstInputRef.focus();
    }
  };

  return (
    <div className="signin-bg">
      <div className="signin-box">
        <h1 className="form">
          <div style={{ color: "#75006f" }}>
            <span>Enter OTP</span>
          </div>
        </h1>
        <br />
        <br />
        <div>
          {Object.keys(formData).map((key, index) => (
            <React.Fragment key={key}>
              <input
                ref={inputRefs[key]}
                name={key}
                type="text"
                style={{
                  width: "40px",
                  height: "50px",
                  fontSize: "20px",
                  border: "2px solid #75006f",
                  outline: "none",
                  textAlign: "center", // Center the text
                }}
                value={formData[key]}
                onChange={(e) => handleInputChange(e, key)}
                maxLength="1"
              />
              &nbsp; {/* Add a space here */}
            </React.Fragment>
          ))}
        </div>
        <br />
        <br />
        <button className="signin-button" type="submit" onClick={handleClear}>
          Clear
        </button>
        <br />
        <button className="signin-button" type="submit" onClick={handleSubmit}>
          Resend
        </button>
        <br />
        <br />
        <h5 style={{ color: "red" }}>Enter the OTP sent to your email</h5>
      </div>
    </div>
  );
}

export default Verification;
