import React, { useState, useRef, useEffect } from "react";
import "../Signin/Signin.css";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Modal from "./Modal";

function Verification() {
  const [open, setOpen] = useState(false);
  const [title, setTitle] = useState("");
  const [subtitle, setSubtitle] = useState("");
  const [status, setStatus] = useState(true);
  const location = useLocation();
  const navigate = useNavigate();
  let username = location.state ? location.state.username : null;
  const [data, setData] = useState();
  const [formData, setFormData] = useState({
    otp1: "",
    otp2: "",
    otp3: "",
    otp4: "",
    otp5: "",
    otp6: "",
  });
  const [resendEnabled, setResendEnabled] = useState(true);
  const [timer, setTimer] = useState(60);

  const inputRefs = {
    otp1: useRef(null),
    otp2: useRef(null),
    otp3: useRef(null),
    otp4: useRef(null),
    otp5: useRef(null),
    otp6: useRef(null),
  };

  useEffect(() => {
    let interval;

    if (resendEnabled && timer > 0) {
      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
      }, 1000);
    }

    return () => {
      clearInterval(interval);
    };
  }, [resendEnabled, timer]);

  const getNextInputName = (currentInputName) => {
    const keys = Object.keys(formData);
    const currentIndex = keys.indexOf(currentInputName);

    if (currentIndex < keys.length - 1) {
      return keys[currentIndex + 1];
    }

    return null;
  };

  const handleInputChange = (event, inputName) => {
    const { value } = event.target;

    if (value === "" && event.key === "Backspace") {
      const prevInputName = getNextInputName(inputName);
      if (prevInputName) {
        setFormData((prevData) => ({
          ...prevData,
          [inputName]: value,
        }));
        inputRefs[prevInputName].current.focus();
      }
    } else {
      const limitedValue = value.slice(0, 1);

      setFormData((prevData) => ({
        ...prevData,
        [inputName]: limitedValue,
      }));

      const nextInputName = getNextInputName(inputName);
      if (nextInputName !== null) {
        inputRefs[nextInputName].current.focus();
      }
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const otp =
      formData.otp1 +
      formData.otp2 +
      formData.otp3 +
      formData.otp4 +
      formData.otp5 +
      formData.otp6;
    const url = "http://localhost:5500/User-Data/verifyOTP";

    const obj = { username, otp };

    axios
      .post(url, obj)
      .then((res) => {
        if (res.status === 200) {
          setTitle("Sign Up Successfull!!");
          setSubtitle("Redirecting to Login Page..");
          setStatus(true);
          setOpen(true);
          navigate("/");
        }
      })
      .catch((err) => {
        if (err.response && err.response.status === 400) {
          setTitle("Sign Up Error!!");
          setSubtitle(err.response.data);
          setStatus(false);
          setOpen(true);
          // alert(err.response.data); // Display the response data from the server
        } else {
          setTitle("Sign Up Error!!");
          setSubtitle(err.response.data);
          setStatus(false);
          setOpen(true);
          // alert("An error occurred: " + err.message);
        }
      });
  };

  const handleClear = () => {
    const firstInputName = Object.keys(formData)[0];
    const firstInputRef = inputRefs[firstInputName].current;

    const clearedFormData = Object.keys(formData).reduce((acc, key) => {
      acc[key] = "";
      return acc;
    }, {});

    setFormData(clearedFormData);

    if (firstInputRef) {
      firstInputRef.focus();
    }
  };

  const handleResend = async () => {
    setResendEnabled(false);
    const obj = { username };
    try {
      const url = "https://music-backend-kinl.onrender.com/Signup-Login/data";
      const response = await axios.post(url, obj);
      setData(response.data);
    } catch (error) {
      console.error("Error fetching history data:", error);
    }
    console.log(data);
    setTimeout(() => {
      setResendEnabled(true);
    }, 60000);
  };

  const cont = () => {
    if (status === true) {
      console.log(formData);
      navigate("/verification", { state: { username } });
    }
    setOpen(false);
  };

  const cancel = () => {
    setOpen(false);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSubmit(event);
    }
  };

  return (
    <>
      <div className="signin-bg">
        <div className="signin-box">
          <h1 className="form">
            <div style={{ color: "#75006f" }}>
              <span>Enter OTP</span>
            </div>
          </h1>
          <h5 style={{ color: "red" }}>Enter the OTP sent to your email</h5>
          <br />
          <div>
            {Object.keys(formData).map((key, index) => (
              <React.Fragment key={key}>
                <input
                  ref={inputRefs[key]}
                  name={key}
                  type="number"
                  style={{
                    width: "40px",
                    height: "50px",
                    fontSize: "20px",
                    border: "2px solid #75006f",
                    outline: "none",
                    textAlign: "center",
                    borderRadius: "5px",
                  }}
                  value={formData[key]}
                  onChange={(e) => handleInputChange(e, key)}
                  maxLength="1"
                />
                &nbsp;
              </React.Fragment>
            ))}
          </div>
          <br />
          <button className="signin-button" type="button" onClick={handleClear}>
            Clear
          </button>
          <br />
          <button
            className="signin-button"
            type="button"
            onClick={handleSubmit}
          >
            Submit
          </button>
          <br />
          <button
            className="signin-button"
            type="button"
            onClick={handleResend}
            disabled={!resendEnabled}
          >
            Resend
          </button>
          <br />
        </div>
      </div>
      <Modal
        open={open}
        closeDialog={cancel}
        title={title}
        subtitle={subtitle}
        deleteFunction={cont}
      />
    </>
  );
}

export default Verification;
