import React, { useState } from "react";
import "./Signin.css";
import Navbar1 from "../Navbar/Navbar";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Modal from "./Modal";
function Signin() {
  const [open, setOpen] = useState(false);
  const [title, setTitle] = useState("");
  const [subtitle, setSubtitle] = useState("");
  const [status, setStatus] = useState(true);
  const location = useLocation();
  let username = location.state ? location.state.username : null;
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const url = "http://localhost:5500/User-Data/create";

    axios
      .post(url, formData)
      .then((res) => {
        if (res.status === 200) {
          setTitle("OTP sent to your Email!!");
          setSubtitle("Enter the OTP to complete Signup..");
          setStatus(true);
          setOpen(true);
        }
      })
      .catch((err) => {
        if (err.response && err.response.status === 400) {
          setTitle("Sign Up Error!!");
          setSubtitle(err.response.data);
          setStatus(false);
          setOpen(true);
          // alert(err.response.data); // Display the response data from the server
        } else {
          setTitle("Sign Up Error!!");
          setSubtitle(err.response.data);
          setStatus(false);
          setOpen(true);
          // alert("An error occurred: " + err.message);
        }
      });
    console.log(formData);
  };

  const cont = () => {
    if (status === true) {
      console.log(formData);
      navigate("/verification", { state: { username } });
    }
    setOpen(false);
  };

  const cancel = () => {
    setOpen(false);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSubmit(event);
    }
  };

  return (
    <>
      <div className="signin-bg">
        <br />
        <Navbar1 />
        <h1>{username}</h1>
        <div className="signin-box">
          <h1 className="form">
            <div className="waviy" style={{ color: "#75006f" }}>
              <span style={{ "--i": 1 }}>L</span>
              <span style={{ "--i": 2 }}>o</span>
              <span style={{ "--i": 3 }}>g</span>
              &nbsp;
              <span style={{ "--i": 4 }}>i</span>
              <span style={{ "--i": 5 }}>n</span>
            </div>
          </h1>
          <div className="line"></div>
          <br />
          <form onSubmit={handleSubmit}>
            <div className="signin-inputbox">
              <input
                className="signin-input"
                type="text"
                required="required"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
              />
              <span>Username</span>
            </div>
            <br />
            <div className="signin-inputbox">
              <input
                className="signin-input"
                type="password"
                required="required"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
              />
              <span>Password</span>
            </div>
            <br />
            <center>
              <button className="signin-button">Log in</button>

              <div style={{ color: "black", fontSize: "20px" }}>
                <br />
                Forgot{" "}
                <a
                  href="/forgot-password"
                  style={{ color: "#75006f", textDecoration: "none" }}
                >
                  Password
                </a>
                ?
              </div>
              <div style={{ color: "black", fontSize: "20px" }}>
                New to website{" "}
                <a
                  href="/signup"
                  style={{ color: "#75006f", textDecoration: "none" }}
                >
                  Sign Up
                </a>{" "}
                Here
              </div>
            </center>
          </form>
        </div>
      </div>
      <Modal
        open={open}
        closeDialog={cancel}
        title={title}
        subtitle={subtitle}
        deleteFunction={cont}
      />
    </>
  );
}

export default Signin;
