import React, { useState, useRef } from "react";
import "../Signin/Signin.css";
import Navbar1 from "../Navbar/Navbar";
import { useLocation, useNavigate } from "react-router-dom";

function Verification() {
  const location = useLocation();
  let username = location.state ? location.state.username : null;
  const [formData, setFormData] = useState({
    otp1: "",
    otp2: "",
    otp3: "",
    otp4: "",
    otp5: "",
    otp6: "",
  });

  const inputRefs = {
    otp1: useRef(null),
    otp2: useRef(null),
    otp3: useRef(null),
    otp4: useRef(null),
    otp5: useRef(null),
    otp6: useRef(null),
  };

  const handleInputChange = (event, inputName) => {
    const { value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [inputName]: value,
    }));

    // Move focus to the next input
    if (value !== "" && inputRefs[inputName].current.nextSibling) {
      inputRefs[inputName].current.nextSibling.focus();
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    console.log(formData);
    // Add your logic to handle the OTP submission
  };

  return (
    <div className="signin-bg">
      <div className="signin-box">
        <h1 className="form">
          <div style={{ color: "#75006f" }}>
            <span>Enter OTP</span>
          </div>
        </h1>
        <br />
        <br />
        <div>
          {Object.keys(formData).map((key, index) => (
            <React.Fragment key={key}>
              <input
                className="signin-input"
                type="number"
                required="required"
                ref={inputRefs[key]}
                name={key}
                style={{ width: "40px", height: "50px", fontSize: "20px" }}
                value={formData[key]}
                onChange={(e) => handleInputChange(e, key)}
                maxLength="1"
              />
              &nbsp; {/* Add a space here */}
            </React.Fragment>
          ))}
        </div>
        <br />
        <br />
        <button className="signin-button" type="submit" onClick={handleSubmit}>
          Resend
        </button>
        <br />
        <br />
        <h5 style={{ color: "red" }}>Enter the OTP sent to your email</h5>
      </div>
    </div>
  );
}

export default Verification;
