import React, { useState, useRef } from "react";
import "../Signin/Signin.css";
import Navbar1 from "../Navbar/Navbar";
import { useLocation, useNavigate } from "react-router-dom";

function Verification() {
  const location = useLocation();
  let username = location.state ? location.state.username : null;
  const [otp, setOtp] = useState(["", "", "", "", "", ""]);
  const inputRefs = Array.from({ length: 6 }, useRef());

  const handleInputChange = (index, event) => {
    if (event.target.value && index < inputRefs.length - 1) {
      inputRefs[index + 1].current.focus();
    }

    setOtp((prevOtp) => {
      const newOtp = [...prevOtp];
      newOtp[index] = value;
      return newOtp;
    });

    const handleResendClick = () => {
      console.log("Resending OTP...");
    };

    const handleSubmit = (event) => {
      event.preventDefault();

      const enteredOtp = otp.join("");
      console.log("Entered OTP:", enteredOtp);
    };

    return (
      <div className="signin-bg">
        <div className="signin-box">
          <h1 className="form">
            <div style={{ color: "#75006f" }}>
              <span>Enter OTP</span>
            </div>
          </h1>
          <form onSubmit={handleSubmit}>
            <br />
            <br />
            <div>
              {otp.map((value, index) => (
                <input
                  key={index}
                  ref={inputRefs[index]}
                  style={{ width: "40px", height: "50px", fontSize: "20px" }}
                  value={value}
                  onChange={(e) => handleInputChange(index, e.target.value)}
                  maxLength={1}
                />
              ))}
            </div>
            <br />
            <br />
            <button className="signin-button" type="submit">
              Resend
            </button>
            <br />
            <br />
            <h5 style={{ color: "red" }}>Enter the OTP sent to your email</h5>
          </form>
        </div>
      </div>
    );
  };
}

export default Verification;
